select DISTINCT b.BUY_NUM,p.PRODUCT_NUM,p.PRODUCT_NAME,m.ID,bs.BUYSTATE_SIZE,bs.BUYSTATE_COLOR,bs.BUYSTATE_EACH,bs.BUYSTATE_PRICE ,bs.BUYSTATE_EXPRESSNUM,bs.BUYSTATE_PAY_DATE,bs.BUYSTATE_RESULT_DATE,bs.BUYSTATE_STATE from PRODUCT p, buy b, BUYSTATE bs , MEMBER m where bs.BUY_NUM = b.BUY_NUM and m.ID = b.ID and p.PRODUCT_NUM = b.PRODUCT_NUM
