mapper.select:
select ps.productSize_size from product p,productSize ps
where p.product_num = ps.product_num
and p.product_num = #{product_num}

select DISTINCT b.BUY_NUM,p.PRODUCT_NUM,p.PRODUCT_NAME,m.ID,bs.BUYSTATE_SIZE,bs.BUYSTATE_COLOR,bs.BUYSTATE_EACH,bs.BUYSTATE_PRICE ,bs.BUYSTATE_EXPRESSNUM,bs.BUYSTATE_PAY_DATE,bs.BUYSTATE_RESULT_DATE,bs.BUYSTATE_STATE,bs.buystate_address from PRODUCT p, buy b, BUYSTATE bs , MEMBER m where bs.BUY_NUM = b.BUY_NUM and m.ID = b.ID and p.PRODUCT_NUM = b.PRODUCT_NUM
and bs.BUYSTATE_STATE = #{state_num}
order by bs.buystate_state asc, bs.buystate_pay_date desc , b.buy_num desc

