mapper.select:
		select productEach_each from productEach
		where product_num=#{product_num}
		and productSize_size = #{productSize_size}
		and productEach_color = #{productEach_color}

select DISTINCT ps.PRODUCTSIZE_SIZE from product p ,PRODUCTKIND pk,kind k, PRODUCTSIZE ps where pk.KIND_NUM = k.KIND_NUM and pk.PRODUCT_NUM = p.PRODUCT_NUM and ps.PRODUCT_NUM = p.PRODUCT_NUM
and k.PRODUCTSELECT_NUM = #{productSelect_num}
order by case when ps.productSize_size = 'SSS' then 1 when ps.productSize_size = 'SS' then 2 when ps.productSize_size = 'S' then 3 when ps.productSize_size = 'M' then 4 when ps.productSize_size = 'L' then 5 when ps.productSize_size = 'XL' then 6 when ps.productSize_size = 'XXL' then 7 end

select DISTINCT pe.PRODUCTEACH_COLOR from PRODUCT p , PRODUCTEACH pe,PRODUCTKIND pk,kind k where pk.KIND_NUM = k.KIND_NUM and pk.PRODUCT_NUM = p.PRODUCT_NUM and pe.PRODUCT_NUM = p.PRODUCT_NUM and pe.PRODUCTEACH_EACH not in(0)
and k.PRODUCTSELECT_NUM = #{productSelect_num}
order by pe.productEach_color asc
